CREATE TABLE qui_artcategories_translation (id BIGINT, name VARCHAR(255) DEFAULT 'Nazwa kategorii' NOT NULL, lang CHAR(2), slug VARCHAR(255), UNIQUE INDEX artcategory_slug_idx (slug, lang, name), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
